import { NextResponse } from 'next/server'
import fs from 'fs'
import path from 'path'

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url)
    const requestedDate = searchParams.get('date')
    const requestedWeek = searchParams.get('week')
    
    // Read predictions from the JSON file generated by nfl_daily_predictor.py
    const filePath = path.join(process.cwd(), 'nfl_daily_predictions.json')
    
    if (fs.existsSync(filePath)) {
      const fileData = fs.readFileSync(filePath, 'utf8')
      const allPredictions = JSON.parse(fileData)
      
      // Filter by week if requested (prioritize week over date for NFL)
      if (requestedWeek) {
        const week = parseInt(requestedWeek)
        const filtered = allPredictions.filter((p: any) => p.week === week)
        return NextResponse.json({ predictions: filtered })
      }
      
      // Filter by date if requested
      if (requestedDate) {
        const filtered = allPredictions.filter((p: any) => p.date === requestedDate)
        return NextResponse.json({ predictions: filtered })
      }
      
      // Return current week (most recent week) by default
      const weeks = [...new Set(allPredictions.map((p: any) => p.week).filter((w: any) => w))].sort((a: number, b: number) => b - a)
      const currentWeek = weeks[0] || 14
      const currentWeekPredictions = allPredictions.filter((p: any) => p.week === currentWeek)
      
      return NextResponse.json({ predictions: currentWeekPredictions })
    }
    
    // Return empty if file doesn't exist
    return NextResponse.json({
      predictions: [],
      message: 'No predictions available. Run: python3 nfl_daily_predictor.py'
    })
  } catch (error) {
    console.error('Error reading NFL predictions:', error)
    return NextResponse.json({
      predictions: [],
      message: 'Unable to fetch predictions at this time'
    })
  }
}

