import { NextResponse } from 'next/server'
import fs from 'fs'
import path from 'path'

export async function GET() {
  try {
    // Read predictions from the JSON file generated by daily_predictor.py
    const filePath = path.join(process.cwd(), 'daily_predictions.json')
    
    if (fs.existsSync(filePath)) {
      const fileData = fs.readFileSync(filePath, 'utf8')
      const predictions = JSON.parse(fileData)
      return NextResponse.json({ predictions })
    }
    
    // Return empty if file doesn't exist
    return NextResponse.json({
      predictions: [],
      message: 'No predictions available. Run: python3 daily_predictor.py'
    })
  } catch (error) {
    console.error('Error reading predictions:', error)
    return NextResponse.json({
      predictions: [],
      message: 'Unable to fetch predictions at this time'
    })
  }
}

